(function(){tinymce.create('tinymce.plugins.FlagsPlugin',{init:function(ed,url){ed.addCommand('mceFlag',function(){ed.windowManager.open({file:url+'/flags.htm',width:250+parseInt(ed.getLang('flags.delta_width',0)),height:160+parseInt(ed.getLang('flags.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('flags',{title:'Flags',cmd:'mceFlag',label:'flags'})},getInfo:function(){return{longname:'Flags',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/flags',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('flags',tinymce.plugins.FlagsPlugin)})();
